int checkPW(char* input, char* pw){
    int j;
    for(j = 0; j < SIZE-1; j++){
        // Conferir se o input é equivalente ao char - 3 da senha
        if((pw[j] - 3) != input[j]){
            return 0;
        }
    }
    return 1;
}

